import { useContext, useEffect, useState } from "react";
import { useParams, useNavigate } from 'react-router-dom';

import * as movieService from '../../services/movieService';
import { MovieContext } from "../../contexts/MovieContext";
import { Header } from "../Header";
import { Footer } from "../Footer";

export const EditMovie = () => {
    const [currentMovie, setCurrentMovie] = useState({});
    const { movieEdit } = useContext(MovieContext);
    const { movieId } = useParams();
    const navigate = useNavigate();
    useEffect(()=>{document.getElementById('catalog').classList.add('active')},[])

    useEffect(() => {
        movieService.getOne(movieId)
            .then(movieData => {
                setCurrentMovie(movieData);
            })
    }, [])

    const onSubmit = (e) => {
        e.preventDefault();

        const movieData = Object.fromEntries(new FormData(e.target));

        movieService.edit(movieId, movieData)
            .then(result => {
                movieEdit(movieId, result);
                navigate(`/details/${movieId}`)
            });
    };

    return (
        <>
        <Header />
        <section id="edit-page" className="auth">
            <form id="edit" onSubmit={onSubmit}>
                <div className="container">
                    <h1>Edit Movie</h1>
                    <label htmlFor="title">Movie title:</label>
                    <input type="text" id="title" name="title" value={currentMovie.title} onChange={(e)=>setCurrentMovie(FormData(e.target.parentNode))}/>

                    <label htmlFor="img">Image:</label>
                    <input type="text" id="img" name="img" value={currentMovie.img} onChange={(e)=>setCurrentMovie(FormData(e.target.parentNode))}/>
                    <label htmlFor="description">Movie description:</label>
                    <textarea name="description" id="description" value={currentMovie.description} onChange={(e)=>setCurrentMovie(FormData(e.target.parentNode))}/>
                    <input className="btn" type="submit" value="Edit Movie" />
                    <button onClick={(e)=>{e.preventDefault();navigate((`/details/${movieId}`))}} className="btn">Go back</button>
                </div>
            </form>
        </section>
        <Footer />
        </>
    );
}
