import { useContext } from 'react';

import { MovieContext } from '../../contexts/MovieContext';
import * as movieService from '../../services/movieService';

export const CreateMovie = () => {
    const { movieAdd } = useContext(MovieContext);

    const onSubmit = (e) => {
        e.preventDefault();

        const movieData = Object.fromEntries(new FormData(e.target));

        movieService.create(movieData)
            .then(result => {
                movieAdd(result)
            });
    };

    return (
        <section id="create-page" className="auth">
            <form id="create" onSubmit={onSubmit}>
                <div className="container">
                    <h1>Add Movie</h1>
                    <label htmlFor="title">Movie title:</label>
                    <input type="text" id="title" name="title"/>


                    <label htmlFor="movie-poster">Image:</label>
                    <input type="text" id="movie-poster" name="movie-poster"/>

                    <label htmlFor="description">Movie description:</label>

                    <textarea name="description" id="description" />

                    <input className="btn" type="submit" value="Add Movie"/>
                </div>
            </form>
        </section>
    );
};
