import { useContext, useEffect, useState } from "react";
import { useParams, useNavigate } from 'react-router-dom';

import * as movieService from '../../services/movieService';
import { MovieContext } from "../../contexts/MovieContext";

export const EditMovie = () => {
    const [currentMovie, setCurrentMovie] = useState({});
    const { movieEdit } = useContext(MovieContext);
    const { movieId } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        movieService.getOne(movieId)
            .then(movieData => {
                setCurrentMovie(movieData);
            })
    }, [])

    const onSubmit = (e) => {
        e.preventDefault();

        const movieData = Object.fromEntries(new FormData(e.target));

        movieService.edit(movieId, movieData)
            .then(result => {
                movieEdit(movieId, result);
                navigate(`/catalog/${movieId}`)
            });
    };

    return (
        <section id="edit-page" className="auth">
            <form id="edit" onSubmit={onSubmit}>
                <div className="container">
                    <h1>Edit Game</h1>
                    <label htmlFor="leg-title">Legendary title:</label>
                    <input type="text" id="title" name="title" defaultValue={currentGame.title} />
                    <label htmlFor="category">Category:</label>
                    <input type="text" id="category" name="category" defaultValue={currentGame.category} />
                    <label htmlFor="levels">MaxLevel:</label>
                    <input
                        type="number"
                        id="maxLevel"
                        name="maxLevel"
                        min={1}
                        defaultValue={currentGame.maxLevel}
                    />
                    <label htmlFor="game-img">Image:</label>
                    <input type="text" id="imageUrl" name="imageUrl" defaultValue={currentGame.imageUrl} />
                    <label htmlFor="summary">Summary:</label>
                    <textarea name="summary" id="summary" defaultValue={currentGame.summary} />
                    <input className="btn submit" type="submit" defaultValue="Edit Game" />
                </div>
            </form>
        </section>
    );
}
